package net.nczonline.web.cssembed;

import java.io.BufferedReader;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.Reader;

import junit.framework.Assert;

import org.apache.tools.ant.BuildFileTest;
import org.junit.Test;

/**
 * @author Phil Mander
 *
 */
public class CSSEmbedAntTaskTest extends BuildFileTest
{

    String[] expectedOutput =
    {
            "body {",
            "background: url(data:image/jpeg;base64,);",
            "}" };

    @Override
    protected void setUp() throws Exception
    {
        configureProject("cssembed.xml");
    }

    @Test
    public void testMain() throws IOException
    {
        executeTarget("test");

        String sep = System.getProperty("file.separator");
        String destDir = getProjectDir().toString() + sep + "anttask" + sep + "temp" + sep + "dest";
        File resultFile = new File(destDir, "test-datauri.css");

        Assert.assertTrue("Generated file does not exist", resultFile.exists());

        Reader result = new InputStreamReader(new FileInputStream(resultFile), "ISO-8859-1");
        BufferedReader reader = new BufferedReader(result);
        String line = null;
        int lineNum = 0;

        while ((line = reader.readLine()) != null)
        {
            String expected = expectedOutput[lineNum].trim();
            Assert.assertEquals("Line " + lineNum + 1 + " does not equal " + expected, line.trim(), expected);
            lineNum++;
        }
    }

    @Override
    protected void tearDown() throws Exception
    {
        executeTarget("clean");
    }
}
